SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_CHILDPROJECTCHILDRENLIST(@CONTEXTID uniqueidentifier) as set nocount on;
select FIELDOFFICEID, so.ID SPONSORSHIPOPPORTUNITYID, so.LOOKUPID CHILDLOOKUPID, so_child.NAME, ch_ext.REGISTRATIONDATE, so_child.AGE, so_child.GENDER, dbo.UFN_DATE_FROMFUZZYDATE (so_child.BIRTHDATE) BIRTHDATE, so.AVAILABILITY, so.ELIGIBILITY, case when dbo.UFN_SPONSORSHIPOPPORTUNITY_ACTIVESPONSORSHIPS(so.ID) > 0 then 'Sponsored' when AVAILABILITYCODE = 1 then 'Unsponsored - Reserved' else 'Unsponsored' end as SPONSORSHIPSTATUS, so_reserveprocess.NAME as RESERVATIONKEY
from dbo.SPONSORSHIPOPPORTUNITY so inner join dbo.SPONSORSHIPOPPORTUNITYCHILD so_child on so.ID = so_child.ID left join dbo.USR_CHILDEXTENSION ch_ext on so.ID = ch_ext.SPONSORSHIPOPPORTUNITYCHILDID left join dbo.SPONSORSHIPOPPORTUNITYRESERVEPROCESS so_reserveprocess on so_reserveprocess.ID = so.RESERVATIONKEYID left join dbo.SPONSORSHIPLOCATION slocation ON slocation.ID = so.SPONSORSHIPLOCATIONID where FIELDOFFICEID = @CONTEXTID
