			PRIMARYOCCUPATIONOTHER = @PRIMARYOCCUPATIONOTHER, 
			PRIMARYOCCUPATIONOTHERDESC = @PRIMARYOCCUPATIONOTHERDESC, 
			AVGHOURSPERWEEK = @AVGHOURSPERWEEK, 
			UNEMPLOYMENTRATE = @UNEMPLOYMENTRATE, 
			LOCALTRADITIONS = @LOCALTRADITIONS, 
			HOLIDAYS = @HOLIDAYS, 
