			PRIMARYLANGUAGES = @PRIMARYLANGUAGES, 
			FAMILYSTRUCTUREPARTRIARCHAL = @FAMILYSTRUCTUREPARTRIARCHAL, 
			FAMILYSTRUCTUREMATRIARCHAL = @FAMILYSTRUCTUREMATRIARCHAL, 
