create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_CHILD_PROJECT_EXTENSION ( @ID uniqueidentifier = null output, @CHANGEAGENTID uniqueidentifier = null, @NAME nvarchar(100) = '', @LOOKUPID nvarchar(36) = '',
@CHILDPROJECTTYPECODEID uniqueidentifier, @CHILDPROJECTTYPEOTHER nvarchar(50) = '', @CHILDPROJECTSTATUSCODEID uniqueidentifier = null, @CLOSUREDATE date = null, @CHILDPROJECTCATEGORYCODEID uniqueidentifier = null, @CHILDPROJECTSUBCATEGORYCODEID uniqueidentifier = null
) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
declare @childProjectConstituencyCode uniqueidentifier
begin try select @childProjectConstituencyCode = ID from CONSTITUENCYCODE where lower(DESCRIPTION) = 'child project' if @childProjectConstituencyCode is null RAISERROR 1000000 'Could not find Child Project constituency code'
exec dbo.USP_ORGANIZATION_ADD @ID output, @CHANGEAGENTID, @CHANGEAGENTID, @NAME, '', --@WEBADDRESS NULL, --@PICTURE, NULL, --@PICTURETHUMBNAIL, NULL, --@INDUSTRYCODEID, NULL, --@NUMEMPLOYEES, NULL, --@NUMSUBSIDIARIES, NULL, --@PARENTCORPID, NULL, --@ADDRESS_ADDRESSTYPECODEID, 0, --@ADDRESS_DONOTMAIL, NULL, --@ADDRESS_COUNTRYID, NULL, --@ADDRESS_STATEID, NULL, --@ADDRESS_ADDRESSBLOCK, NULL, --@ADDRESS_CITY, NULL, --@ADDRESS_POSTCODE, NULL, --@PHONE_PHONETYPECODEID, NULL, --@PHONE_NUMBER, NULL, --@SKIP_ADDING_SECURITYGROUPS, NULL, --@EMAILADDRESS_EMAILADDRESSTYPECODEID, NULL, --@EMAILADDRESS_EMAILADDRESS, 0, --@OMITFROMVALIDATION, NULL, --@CART, NULL, --@DPC, NULL, --@LOT, NULL, --@COUNTYCODEID, NULL, --@CONGRESSIONALDISTRICTCODEID, NULL, --@LASTVALIDATIONATTEMPTDATE, '', --@VALIDATIONMESSAGE, 0, --@CERTIFICATIONDATA, NULL, --@ADDRESS_DONOTMAILREASONCODEID, 1, --@ISPRIMARY, NULL, --@SKIP_ADDING_SITES, NULL, --@INFOSOURCECODEID, 1;
-- Change the LookupID to the one entered by the user update CONSTITUENT set CUSTOMIDENTIFIER = @LOOKUPID where ID = @ID;
-- Add the Child Project constituency code if @childProjectConstituencyCode is not null exec dbo.USP_DATAFORMTEMPLATE_ADD_CONSTITUENCY @CHANGEAGENTID = @CHANGEAGENTID, @CONSTITUENTID = @ID, @CONSTITUENCYCODEID = @childProjectConstituencyCode;
-- handle inserting the data insert into dbo.USR_CHILDPROJECTEXTENSION (ID, CHILDPROJECTTYPECODEID, CHILDPROJECTTYPEOTHER, CHILDPROJECTSTATUSCODEID, CLOSUREDATE, CHILDPROJECTCATEGORYCODEID, CHILDPROJECTSUBCATEGORYCODEID, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) values (@ID, @CHILDPROJECTTYPECODEID, @CHILDPROJECTTYPEOTHER, @CHILDPROJECTSTATUSCODEID, @CLOSUREDATE, @CHILDPROJECTCATEGORYCODEID, @CHILDPROJECTSUBCATEGORYCODEID, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE)
end try begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
return 0
